import React, { useState } from 'react';
import Grid from './components/Grid';


function App() {
  const [days, setDates] = useState(0);
  const [form, setForm] = useState("");

  const daysOfMonth = () => {

  }

  const monthsOfYear = ["January", "February", "March", "April", "June", "July", "August", "September", "October", "November", "December"]
  const yearsToCome = () => {
    let currentYear = new Date().getFullYear()
    return [...Array(2050 - currentYear + 1).keys()].map(i => i + currentYear);
  }

  const onChange = (e) => {
    const { name, value } = e.target;

    setForm((state) => ({ ...state, [name]: value }));
  };

  return (
    <div className="container">
      <h1>Time Sheet</h1>
      <form onSubmit="">
      <div className="row">
        <div className="col">
          <select className="form-select" aria-label="Default select example">
            <option defaultValue>Select Month</option>
            {monthsOfYear.map(month => {
              return <option key={month} name={month} value={month}>{month}</option>
            })}
          </select>
        </div>
        <div className="col">
          <select className="form-select" aria-label="Default select example">
            <option defaultValue>Select Year</option>
            {yearsToCome().map(year => {
              return <option key={year} name={year} value={year}>{year}</option>
            })}
          </select>
        </div>
        <div className="col">
          <button type="button" class="btn btn-success">Generate!</button>
        </div>
      </div>
      </form>
      <Grid></Grid>
    </div>
  );
}

export default App;
