import React, { useState } from 'react';
import Grid from './components/Grid';


function App() {
  const [days, setDates] = useState(0);
  const [month, setMonth] = useState(new Date().getMonth());
  const [year, setYear] = useState(new Date().getFullYear())

  const daysOfMonth = (month, year) => {
    let date = new Date(year, month, 1);
    let days = [];
    while (date.getMonth() === month) {
      let day = new Date(date).toLocaleDateString()
      days.push(day);
      date.setDate(date.getDate() + 1);
    }
    return days;
  }

  const monthsOfYear = ["January", "February", "March", "April", "June", "July", "August", "September", "October", "November", "December"]
  const yearsToCome = () => {
    let currentYear = new Date().getFullYear()
    return [...Array(2050 - currentYear + 1).keys()].map(i => i + currentYear);
  }

  return (
    <div className="container">
      <h1>Time Sheet</h1>
      <div className="row">
        <div className="col">
          <select  onChange={(e) => setMonth(e.target.value)} className="form-select" aria-label="Default select example">
            <option defaultValue>Select Month</option>
            {monthsOfYear.map(month => {
              return <option key={month} name={month} value={month}>{month}</option>
            })}
          </select>
        </div>
        <div className="col">
          <select onChange={(e) => setYear(e.target.value)}className="form-select" aria-label="Default select example">
            <option defaultValue>Select Year</option>
            {yearsToCome().map(year => {
              return <option key={year} name={year} value={year}>{year}</option>
            })}
          </select>
        </div>
        <div className="col">
          <button type="submit" className="btn btn-success">Generate!</button>
        </div>
      </div>
      <Grid></Grid>
    </div>
  );
}

export default App;
