import React from 'react'
import { useState, useRef } from 'react'


const Grid = ({allDaysOfMonth}) => {
  const timeIn = useRef();
  const timeOut = useRef();

  const calculateTime = (cell) =>{
   
  }

  const calculateTotal = (e) =>{
      console.log(e.target.value)
  }
  const isWeekend =(day)=>{
    if (day.includes("Sat") || day.includes("Sun")){
      return true
    }
  }
  const startTime = document.getElementsByClassName('timeIn')
  const endTime = document.getElementsByClassName('timeOut')


  return (

    <table className="table">
      <thead className="thead-dark">
        <tr>
          <th scope="col">Month</th>
          <th scope="col">Time In</th>
          <th scope="col">Time Out</th>
          <th scope="col">Hours</th>
          <th scope="col">Notes</th>
        </tr>
      </thead>
      <tbody>
        {allDaysOfMonth && allDaysOfMonth.map((day, cell) => {
          return (
            <tr key={cell}>
              <th scope="row">{day}</th>
              <td ref={timeIn} className='timeIn' style={{background: isWeekend(day) ? 'grey' : 'white' }}>{ !isWeekend(day) && <input onChange={(e)=> calculateTotal(e)} className="form-control" type="text" placeholder="Time In"/>}</td>
              <td ref={timeOut} className='timeOut'style={{background: isWeekend(day) ? 'grey' : 'white' }}>{ !isWeekend(day) && <input className="form-control" type="text" placeholder="Time Out"/>}</td>
              <td></td>
              <td></td>
            </tr>
          )
        })}
      </tbody>



    </table>

  )
}

export default Grid
