import React from 'react'
import { useState, useEffect } from 'react'


const Grid = ({month,year}) => {
  const [allDaysOfMonth, setDaysOfMonth] = useState();

useEffect(() => {
  daysOfMonth()
  
},[month, year]);

console.log('daysofmonth',allDaysOfMonth)

const daysOfMonth = (month, year) => {
  let date = new Date(year, month, 1);
  let days = [];
  while (date.getMonth() === month) {
    let day = new Date(date).toLocaleDateString()
    days.push(day);
    date.setDate(date.getDate() + 1);
  }
  setDaysOfMonth(daysOfMonth(month, year));
}

  return (
    <table className="table">
      <thead className="thead-dark">
        <tr>
          <th scope="col">Month</th>
          <th scope="col">Time In</th>
          <th scope="col">Time Out</th>
          <th scope="col">Hours</th>
          <th scope="col">Notes</th>
        </tr>
      </thead>
      <tbody>
        {allDaysOfMonth && allDaysOfMonth.map((day, cell) => {
          return (
            <tr key={day}>
              <th scope="row">{day}</th>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
            </tr>
          )
        })}
      </tbody>



    </table>

  )
}

export default Grid
